CREATE DATABASE /*!32312 IF NOT EXISTS*/ php_carrito;
USE php_carrito;

--
-- Table structure for table `php_carrito`.`noticias`
--

DROP TABLE IF EXISTS `noticias`;
CREATE TABLE `noticias` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `falta` datetime NOT NULL COMMENT 'Fecha',
  `fmodificacion` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT 'F. Modificacion',
  `habilitado` tinyint(1) unsigned DEFAULT '1' COMMENT 'Habilitado',
  `feliminado` datetime DEFAULT NULL COMMENT 'F. Elimimado',
  `titulo` varchar(255) NOT NULL DEFAULT '' COMMENT 'Titulo',
  `volanta` varchar(255) NOT NULL COMMENT 'Volanta',
  `copete` text COMMENT 'Copete',
  `autor` varchar(255) DEFAULT NULL COMMENT 'Autor',
  `contenido` text COMMENT 'Contenido',
  PRIMARY KEY (`id`),
  KEY `falta` (`falta`)
) ENGINE=InnoDB AUTO_INCREMENT=15 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

